import React from "react";
import ReactDOM from "react-dom";
import { useSpring, animated, interpolate, config } from "react-spring";
import { useGesture } from "react-with-gesture";
import styled from "styled-components";

const Container = styled.div`
  & {
    .item {
      position: relative;
      width: 300px;
      height: 100px;
      pointer-events: auto;
      transform-origin: 50% 50% 0px;
      padding-left: 32px;
      padding-right: 32px;
      box-sizing: border-box;
      display: grid;
      align-items: center;
      text-align: center;
      border-radius: 5px;
      box-shadow: 0px 10px 10px -5px rgba(0, 0, 0, 0.2);
    }

    .fg {
      cursor: -webkit-grab;
      background-color: #272727;
      color: rgba(255, 255, 255, 0.8);
      position: absolute;
      height: 100%;
      width: 100%;
      display: grid;
      align-items: center;
      text-align: center;
      border-radius: 5px;
      box-shadow: 0px 10px 30px -5px rgba(0, 0, 0, 0.2);
      font-size: 3em;

      font-weight: 800;
      transition: box-shadow 0.75s;
    }

    .fg:active {
      cursor: -webkit-grabbing;
      box-shadow: 0px 60px 50px -5px rgba(0, 0, 0, 0.4);
    }

    .fg > * {
      pointer-events: none;
    }

    .av {
      width: 60px;
      height: 60px;
      border-radius: 50%;
      background-color: white;
    }
  }
`;

function Slider({ children }) {
  // See: https://github.com/drcmda/react-with-gesture
  // Gives access to: down, x, y, xDelta, yDelta, xInitial, yInitial
  const [handlers, { xDelta, down }] = useGesture();
  const [{ x, y, bg, pers, size }] = useSpring({
    x: down ? xDelta : 0,
    y: down ? -30 : 0,
    pers: `perspective(800px) rotateX(${down ? 45 : 0}deg)`,
    bg: `linear-gradient(120deg, ${
      xDelta < 0 ? "#f093fb 0%, #f5576c" : "#96fbc4 0%, #f9f586"
    } 100%)`,
    size: down ? 1.15 : 1,
    immediate: (name) => down && name === "x",
  });
  const avSize = x.interpolate({
    map: Math.abs,
    range: [50, 300],
    output: ["scale(0.5)", "scale(1)"],
    extrapolate: "clamp",
  });
  return (
    <animated.div
      {...handlers}
      class="item"
      style={{ background: bg, transform: pers }}
    >
      <animated.div
        class="av"
        style={{ transform: avSize, justifySelf: xDelta < 0 ? "end" : "start" }}
      />
      <animated.div
        class="fg"
        style={{
          transform: interpolate(
            [x, y, size],
            (x, y, s) => `translate3d(${x}px,${y}px,0) scale(${s})`
          ),
        }}
      >
        <i>{children}</i>
      </animated.div>
    </animated.div>
  );
}
