import React from "react";
import styled from "styled-components";
const Wrapper = styled.span`
  position: relative;
  width: fit-content;
  height: fit-content;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
`;
const Content = styled.span`
  width: fit-content;
  height: fit-content;
  position: absolute;
  ${props => `${props.direction}: -${props.position}px`};
  background-color: #546e7a;
  color: #ffffff;
  font-weight: 500;
  font-size: 14px;
  border-radius: 10px;
  padding: 4px;
`;
export default function Tooltip({
  content,
  direction,
  position,
  delay = 300,
  children
}) {
  const [show, setShow] = React.useState(false);

  const onMouseOver = () => {
    setTimeout(() => setShow(true), delay);
  };

  const onMouseOut = () => setShow(false);

  return /*#__PURE__*/React.createElement(Wrapper, {
    onMouseOver: onMouseOver,
    onMouseOut: onMouseOut
  }, show ? /*#__PURE__*/React.createElement(Content, {
    direction: direction,
    position: position
  }, content) : null, children);
}
