import React from "react";
import SideBar from "@yoru_cha/yoru-ui-react/dist/SideBar";
import AceEditor from "react-ace";
import IconButton from "@yoru_cha/yoru-ui-react/dist/IconButton";
import Icon from "@yoru_cha/yoru-ui-react/dist/Icon";
import "ace-builds/src-noconflict/mode-javascript";
import "ace-builds/src-noconflict/theme-chaos";
import "ace-builds/src-noconflict/ext-language_tools";
import { Wrapper, Container, ContainerNav } from "../style";
import { text } from "../../text/HeaderBar";

export default function SideBarPage() {
  const [simpleOpen, setSimpleOpen] = React.useState(false);
  const [openSideBar, setOpenSideBar] = React.useState(true);
  const title = <div>TITLE</div>;
  const menu = <div>MENU</div>;
  const login = <div>LOGIN</div>;
  return (
    <Wrapper>
      <h1 style={{ fontSize: "48px", fontWeight: "600" }}>Header Bar</h1>
      <h2 style={{ fontSize: "24px", fontWeight: "400" }}>
        The Header Bar displays information and actions relating to the current
        screen
      </h2>
      <h2 style={{ fontSize: "24px", fontWeight: "400" }}>
        It is designed to be user-friendly, and it's easy to use
      </h2>
      <br></br>
      <Container style={{ position: "relative" }}>
        <SideBar
          routingList={[
            {
              title: "Yoru UI",
              content: [{ subTitle: "What is this?", to: "/" }],
            },
            {
              title: "Color List",
              content: [{ subTitle: "Material Color", to: "/materialcolor" }],
            },
            {
              title: "Component",
              content: [
                { subTitle: "Badge", to: "/component/badge" },
                { subTitle: "Button", to: "/component/button" },
                { subTitle: "CheckBox", to: "/component/checkbox" },
                { subTitle: "HeaderBar", to: "/component/headerbar" },
                { subTitle: "Icon", to: "/component/icon" },
                { subTitle: "Radio", to: "/component/radio" },
                { subTitle: "Select", to: "/component/select" },
                { subTitle: "SideBar", to: "/component/sidebar" },
                { subTitle: "TextField", to: "/component/textfield" },
                { subTitle: "ToolTip", to: "/component/tooltip" },
              ],
            },
            {
              title: "TestPage",
              content: [{ subTitle: "Test", to: "/testpage" }],
            },
          ]}
          open={openSideBar}
          onClose={() => setOpenSideBar(false)}
        ></SideBar>
      </Container>
      <Container style={{ position: "relative" }}></Container>
      <ContainerNav>
        <IconButton onClick={() => setSimpleOpen((prev) => !prev)}>
          <Icon>
            <path d="M9.4 16.6L4.8 12l4.6-4.6L8 6l-6 6 6 6 1.4-1.4zm5.2 0l4.6-4.6-4.6-4.6L16 6l6 6-6 6-1.4-1.4z"></path>
          </Icon>
        </IconButton>
      </ContainerNav>
      {simpleOpen ? (
        <AceEditor
          mode="javascript"
          theme="chaos"
          width="64rem"
          height="15rem"
          fontSize={18}
          readOnly={true}
          name="editor-simple-button"
          value={text}
        ></AceEditor>
      ) : null}
    </Wrapper>
  );
}
